def merge_the_tools(string, k):
    substrings = [string[i:i+k] for i in range(0, len(string), k)]

    for str in substrings:
        no_repeats = ""
        for char in str:
            if not char in no_repeats:
                no_repeats += char
        print(no_repeats)
        
    # split string into substrings of length k 
    #remove repeated characters from substrings 
    #print a line displaying each resulting substring 





merge_the_tools('AAABCADDE', 3)
merge_the_tools('', 3452)
